package com.general;

public class CharAt 
{
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub

		// Character String Index Place.. Its Start from Index 0
		String s="Harshad";
		System.out.println(s.charAt(0));//0-> H
		System.out.println(s.charAt(3)); //3-> s
			
	}

}
