package com.general;

public class Length 
{
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		
		// Length of the String
		String s="Harshad";
		System.out.println(s.length());
		
		// String Concate 
		String s1="Harshad" + "Patel";
		System.out.println(s1.length());
	}
}
