package com.general;

public class Stting_Stratwith 
{
	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub

		//String Start With & Start End Character.....
		String s="Harshad";
		
		System.out.println(s.startsWith("Ha")); // Start With 
		System.out.println(s.startsWith("ha")); //Start With
		System.out.println(s.endsWith("ad")); //End With
	}

}
